<?php	defined('ABSPATH') or die('This file can not be loaded directly.');	if ( !comments_open() ) return;?><div style="padding:10px;background-color:#fff;">  <div class="am-u-md-8 am-u-sm-centered">	<?php	date_default_timezone_set(PRC);	$closeTimer = (strtotime(date('Y-m-d G:i:s'))-strtotime(get_the_time('Y-m-d G:i:s')))/86400;	if(get_option('close_comments_for_old_posts') && $closeTimer > get_option('close_comments_days_old')){	?>	<button type="button" class="am-btn am-btn-default am-btn-block">评论关闭</button>	<?php	}else{	?>		<div class="am-g">	  <div class="am-u-md-8 am-u-sm-centered">		<form class="am-form-inline" id="comment-form" role="form" method="post" action="<?php echo get_option('siteurl'); ?>/wp-comments-post.php">		  <div class="am-form-group">			<img class="am-radius" src="<?php if(do_option('config_headImgUrl')!=''){echo do_option('config_headImgUrl');}else{echo 'https://cambrian-images.cdn.bcebos.com/39ceafd81d6813a014e747db4aa6f0eb_1524963877208.jpeg';}?>" width="35" height="35"/>		  </div>		  <div class="am-form-group">			<textarea type="text" name="comment" rows="4" cols="40" placeholder="<?php if(is_user_logged_in()){echo '我要发表评论...';}else{echo '登陆后可评论...';} ?>" id="comment-text" maxLength="140" class="am-form-field am-input-sm"></textarea>		  </div>		  <div class="am-form-group">			  <button type="submit" class="am-btn am-btn-warning am-radius am-btn-xs">评论</button>			  <input type="hidden" id="comment-login" data-login="<?=is_user_logged_in();?>"></p>			  <?php comment_id_fields(); do_action('comment_form', $post->ID); ?>			  <input type="hidden" name="pid" id="comment-pid" value="0" size="22" tabindex="1"/>		  </div>		</form>	  </div>	</div>	<?php	}	?>  </div></div><a name="comments"></a>
<?php	if ( have_comments() ) {?>	<ul class="am-comments-list" style="padding:10px;background-color:#fff;">		<?php		wp_list_comments( array(			'type'      => 'comment',			'callback'      => 'tle_comment_list',			'reverse_top_level' => true,			'reverse_children'  => true		) );		?>	</ul>	<div><?php paginate_comments_links();?></div><?php } ?><div class="am-modal am-modal-prompt" tabindex="-1" id="replydialog"><form class="am-form" id="reply-form" method="post" action="<?php echo get_option('siteurl'); ?>/wp-comments-post.php">  <div class="am-modal-dialog">    <div class="am-modal-hd">评论</div>    <div class="am-modal-bd">	      <input type="text" name="comment" id="reply-text" class="am-modal-prompt-input">    	</div>    <div class="am-modal-footer">      <span class="am-modal-btn">		<button type="button" class="am-btn am-btn-warning am-radius am-btn-sm" data-am-modal-cancel>取消</button>	  </span>      <span class="am-modal-btn">		<?php comment_id_fields(); do_action('comment_form', $post->ID); ?>		<input type="hidden" name="comment_parent" id="reply-pid" value="0" size="22" tabindex="1"/>		<button type="submit" class="am-btn am-btn-warning am-radius am-btn-sm" data-am-modal-confirm>提交</button>	  </span>    </div>  </div></form></div><!-- end post comment --><script>$(function() {	$('#comment-form').submit(function(){		if($('#comment-text').val()==''){			return false;		}		if($('#comment-login').attr('data-login')!=1){			alert('登陆后方可评论');			return false;		}	});	$('#reply-form').submit(function(){		if($('#reply-text').val()==''){			return false;		}	});	$(".replyfloor").each(function(){		var id=$(this).attr("id");		$("#"+id).click( function () {			if($('#comment-login').attr('data-login')!=1){				alert('登陆后方可评论');				return;			}			$('#reply-pid').val($(this).attr('data-coid'));			$('#replydialog').modal({			  relatedTarget: this,			  onConfirm: function(e) {				$('#reply-form').submit();			  },			  onCancel: function(e) {			  }			});		});	});});</script>